document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector("#search-open"),t=document.querySelector("#header-search-form");e.addEventListener("click",(()=>{t.classList.toggle("search-form--open")}));const c=document.querySelector("#menu-toggle"),s=document.querySelector("#header-menu");document.body,c&&c.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),s.classList.remove("active")):(c.classList.add("active"),s.classList.add("active"))}))}));