document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector("#search-open"),t=document.querySelector("#header-search-form");e.addEventListener("click",(()=>{t.classList.toggle("search-form--open")}));const c=document.querySelector("#menu-toggle"),s=document.querySelector("#header-menu"),o=document.body;c&&c.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),s.classList.remove("active")):(c.classList.add("active"),s.classList.add("active"))})),new Swiper(".header-slider",{loop:!0,speed:1e3,effect:"fade",lazy:!0,pagination:{el:".swiper-pagination",clickable:!0}}),new Swiper(".cta-slider",{loop:!0,speed:1e3,effect:"fade",lazy:!0,pagination:{el:".swiper-pagination",clickable:!0}});const a=document.querySelector(".toggle-theme");if(a){const e=a.querySelector(".toggle-theme__input");a.addEventListener("click",(()=>{e.checked?o.classList.add("green-theme"):o.classList.remove("green-theme")}))}const i=document.querySelector(".back-top");i&&window.addEventListener("scroll",(()=>{window.scrollY>500?i.classList.add("back-top--visible"):i.classList.remove("back-top--visible")}))}));